const HedgehogLogoSVG = "data:image/svg+xml,"

type TieredNodes struct {
	Spine    []Node
	Leaf     []Node
	Server   []Node
	Gateway  []Node
	External []Node
	Unused   []Node
}

type serverConnection struct {
	primaryLeaf   string
	secondaryLeaf string
	connTypes     map[string][]string // leaf -> connection types
	mclagPair     string
	eslagPair     string
}

type NodeMetrics struct {
	ConnectionCount   int
	AngleDistribution map[int]int
	ParentConnections []string
	ChildConnections  []string
}

